		else
		{

			try
			{
				expenseObj.SiteID = siteSelected;
			}
			catch (Exception ex)
			{
				expenseObj.SiteID = defaultSiteId;
			}

			try
			{
				expenseObj.PaymentTypeID = int.Parse(paymentTypeSelected);
			}
			catch (Exception ex)
			{
				expenseObj.PaymentTypeID = defaultPaymentTypeId;
			}

			expenseObj.Paid = !GetPaymentType(expenseObj.PaymentTypeID).ReconcileFlag;

			expenseObj.SupplierID = supplierSelected;
			expenseObj.GrossAmount = GrossAmount;
			expenseObj.VATAmount = VatAmount;
			expenseObj.NetAmount = NetAmount;
			expenseObj.EditDate = DateTime.Now;

			expenseObj.Heading = expenseHeadingSelected;

			Period expensePeriod = getPeriod(expenseObj.Date);
			expenseObj.PeriodNo = expensePeriod.PeriodKey;
			expenseObj.PeriodId = expensePeriod.PeriodId;
			expenseObj.Notes = expenseNotes;
			expenseObj.CompanyID = companyId;

			Task d = PostInvoice();
		}

	}

	private PaymentType GetPaymentType(int paymentType)
	{
		return Array.Find(paymentTypes, f => f.Id == paymentType);
	}

	private bool validateNewInvoice()
	{
		bool isValid = true;

		if (supplierSelectedName == null)
		{
			isValid = false;
		}

		if (GrossAmount == 0)
		{
			isValid = false;
		}

		if (NetAmount == 0)
		{
			isValid = false;
		}

		if (expenseObj.Description == null)
		{
			isValid = false;
		}
		return isValid;
	}

	private Period getCurrentPeriod()
	{
		return getPeriod(DateTime.Now);
	}

	private Period getPeriod(DateTime inDateTime)
	{
		Period period = Array.Find<Period>(periods, f => DateTime.Compare(inDateTime, f.StartDate) >= 0 && DateTime.Compare(inDateTime, f.EndDate) <= 0);
		return period;
	}

	protected async Task PostInvoice()
	{
		if (updateFlag)
		{
			await Http.SendJsonAsync(HttpMethod.Put, "/api/Invoices/" + expenseObj.ID, expenseObj);
		}
		else
		{
			expenseObj.CreatedDate = DateTime.Now;
			try
			{
				await Http.SendJsonAsync(HttpMethod.Post, "/api/Invoices/", expenseObj);
			}
			catch (Exception ex)
			{
				System.Diagnostics.Debug.WriteLine("Error: " + ex.Message);

			}
		}
		expenseViews = await Http.GetJsonAsync<InvoiceView[]>("/api/InvoiceViews/Year/" + viewPeriodYearsSelected + "/" + companyId);
		clearInputValues();
		this.StateHasChanged();

	}

	private async Task GetViewGroup(int paymentType)
	{
		viewInvoiceGroups = await Http.GetJsonAsync<ViewInvoiceGroup[]>("/api/ViewInvoiceGroups/PayType/" + paymentType + "/" + companyId);
		this.StateHasChanged();
	}

	private async Task GetAllViewGroups()
	{
		viewInvoiceGroups = await Http.GetJsonAsync<ViewInvoiceGroup[]>("/api/ViewInvoiceGroups/Company/" + companyId);
		this.StateHasChanged();
	}

	private void AddInvoiceNotes()
	{
		expenseNotes = expenseObj.Notes;
		showNotes = true;
		StateHasChanged();
	}
	public void updateInvoiceNotes(string newNotes)
	{
		expenseNotes = newNotes;
	}

	// Edit Method
	private void EditInvoice(InvoiceView expenseView)
	{
		expenseObj = new Invoice(companyId);

		expenseObj.ID = expenseView.InvoiceId;
		expenseObj.InvoiceReference = expenseView.InvoiceReference;
		expenseObj.Description = expenseView.InvoiceDescription;
		expenseObj.GrossAmount = Math.Round(expenseView.InvoiceGross, 2);
		expenseObj.VATAmount = Math.Round(expenseView.InvoiceVAT, 2);
		expenseObj.NetAmount = Math.Round(expenseView.InvoiceNet, 2);
		expenseObj.PaymentTypeID = expenseView.InvoicePaymentId;
		expenseObj.SupplierID = expenseView.InvoiceSupplierId;
		expenseObj.SiteID = expenseView.InvoiceSiteId;
		expenseObj.Notes = expenseView.InvoiceNotes;
		expenseObj.Date = expenseView.InvoiceDate;
		expenseObj.CreatedDate = expenseView.InvoiceCreatedDate;

		updateFlag = true;
		Supplier supplier = Array.Find<Supplier>(suppliers, p => p.Id == expenseObj.SupplierID);
		Site site = Array.Find<Site>(sites, p => p.Id == expenseObj.SiteID);

		siteSelectedName = site.SiteName;
		supplierSelectedName = supplier.Name;
		GrossAmount = expenseObj.GrossAmount;
		VatAmount = expenseObj.VATAmount;
		NetAmount = expenseObj.NetAmount;

		expenseNotes = "init";
		this.StateHasChanged();
	}

	// Delete Method
	protected async Task DeleteInvoice(InvoiceView expenseView)
	{

		await Http.DeleteAsync("/api/Invoices/" + expenseView.InvoiceId);

		expenseViews = await Http.GetJsonAsync<InvoiceView[]>("/api/InvoiceViews/Year/" + viewPeriodYearsSelected + "/" + companyId);
	}


