				if (value != null)
				{
					_supplierSelected = value;
					intSupplierId = int.Parse(_supplierSelected);
					Supplier supplier = Array.Find<Supplier>(suppliers, p => p.Id == intSupplierId);
					supplierSelectedName = supplier.Name;
				}
			}
