		{
			InvoiceHeading tempInvoiceHeading = Array.Find<InvoiceHeading>(invoiceHeadings, f => f.Description == value);
			if (tempInvoiceHeading == null)
			{
				InvoiceHeading invoiceHeading = new InvoiceHeading();
